sourcesContent:
import React, { useState } from 'react';
import {
  Calendar, Clock, Star, MapPin, Phone, User, Settings, Home,
  Users, Wrench, DollarSign, CheckCircle, XCircle, Play, UserCheck,
  UserX, LogIn, UserPlus, Eye, EyeOff, Plus, ChevronRight
} from 'lucide-react';

// --- PREDEFINED DATA ---
const PREDEFINED_USERS = [
  { id: 1, name: "John Doe", email: "john@example.com", password: "password123", phone: "+1234567890", address: "123 Main St, Cityville", role: "user", is_verified: true },
  { id: 2, name: "Jane Smith", email: "jane@example.com", password: "password123", phone: "+0987654321", address: "456 Oak Ave, Townsville", role: "provider", is_verified: true, services: [1, 2] },
  { id: 3, name: "Admin User", email: "admin@example.com", password: "admin123", phone: "+1122334455", address: "789 Admin Blvd, Metropolis", role: "admin", is_verified: true }
];

const SERVICES = [
  { id: 1, name: "Plumbing Service", category: { name: "Home Maintenance" }, base_price_min: 50, base_price_max: 150, provider_id: 2 },
  { id: 2, name: "Electrical Repair", category: { name: "Home Maintenance" }, base_price_min: 60, base_price_max: 200, provider_id: 2 },
  { id: 3, name: "Cleaning Service", category: { name: "Home Care" }, base_price_min: 40, base_price_max: 120, provider_id: null }
];

const INITIAL_BOOKINGS = [
  { id: 1, service_id: 1, user_id: 1, provider_id: 2, date: "2023-06-15", time: "10:00", address: "123 Main St, Cityville", notes: "Leaky faucet in kitchen", status: "Completed" },
  { id: 2, service_id: 2, user_id: 1, provider_id: 2, date: "2023-06-20", time: "14:00", address: "123 Main St, Cityville", notes: "Replace light fixture", status: "Pending" }
];

// --- CUSTOM ICON ---
const LockIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
    <path fillRule="evenodd" d="M5 9V7a5 5 0 0110 0v2a2 2 0 012 2v5a2 2 0 01-2 2H5a2 2 0 01-2-2v-5a2 2 0 012-2zm8-2v2H7V7a3 3 0 016 0z" clipRule="evenodd" />
  </svg>
);

// --- PUBLIC HOME PAGE (LANDING) ---
const PublicHomePage = ({ onLoginClick, onRegisterClick }) => {
  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-50 to-gray-100">
      {/* Hero Section */}
      <header className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4 flex justify-between items-center">
          <div className="flex items-center">
            <div className="bg-gradient-to-r from-blue-500 to-indigo-600 w-10 h-10 rounded-lg flex items-center justify-center mr-3">
              <Wrench className="w-6 h-6 text-white" />
            </div>
            <h1 className="text-2xl font-bold bg-gradient-to-r from-blue-600 to-indigo-700 bg-clip-text text-transparent">ODSM</h1>
          </div>
          <div className="flex space-x-3">
            <button
              onClick={onRegisterClick}
              className="px-4 py-2 text-blue-600 font-medium hover:text-blue-800 transition-colors"
            >
              Register
            </button>
            <button
              onClick={onLoginClick}
              className="px-4 py-2 bg-gradient-to-r from-blue-600 to-indigo-700 text-white rounded-lg hover:from-blue-700 hover:to-indigo-800 shadow-md transition-all"
            >
              Sign In
            </button>
          </div>
        </div>
      </header>

      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-16">
        {/* Hero Content */}
        <div className="text-center mb-20">
          <h1 className="text-4xl md:text-6xl font-extrabold text-gray-900 mb-6">
            On-Demand Service <span className="bg-gradient-to-r from-blue-600 to-indigo-700 bg-clip-text text-transparent">Management</span>
          </h1>
          <p className="text-xl text-gray-600 max-w-2xl mx-auto mb-10">
            Book trusted professionals in minutes. Reliable, fast, and hassle-free service at your doorstep.
          </p>
          <div className="flex justify-center gap-4">
            <button
              onClick={onRegisterClick}
              className="px-8 py-3 bg-gradient-to-r from-green-500 to-teal-600 text-white rounded-lg font-medium hover:from-green-600 hover:to-teal-700 shadow-lg transition-all"
            >
              Get Started
            </button>
            <button
              onClick={onLoginClick}
              className="px-8 py-3 bg-white text-gray-800 border border-gray-300 rounded-lg font-medium hover:bg-gray-50 shadow transition-all"
            >
              Sign In
            </button>
          </div>
        </div>

        {/* Services Preview */}
        <div className="mb-16">
          <h2 className="text-3xl font-bold text-center text-gray-900 mb-12">Popular Services</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {SERVICES.slice(0, 3).map(service => (
              <div key={service.id} className="bg-white rounded-2xl p-6 shadow-lg hover:shadow-xl transition-shadow border border-gray-100">
                <div className="text-4xl mb-4">ðŸ”§</div>
                <h3 className="text-xl font-bold text-gray-900 mb-2">{service.name}</h3>
                <p className="text-gray-600 mb-4">{service.category?.name}</p>
                <p className="text-gray-700 font-semibold">
                  Fromâ‚¹${service.base_price_min}
                </p>
              </div>
            ))}
          </div>
        </div>

        {/* Features */}
        <div className="bg-white rounded-2xl p-8 shadow-lg border border-gray-100">
          <h2 className="text-3xl font-bold text-center text-gray-900 mb-10">Why Choose ODSM?</h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {[
              { icon: <Clock className="w-8 h-8" />, title: "Instant Booking", desc: "Book services in under 60 seconds" },
              { icon: <Star className="w-8 h-8" />, title: "Verified Pros", desc: "All providers background-checked" },
              { icon: <CheckCircle className="w-8 h-8" />, title: "Guaranteed Quality", desc: "100% satisfaction or your money back" }
            ].map((feature, i) => (
              <div key={i} className="text-center">
                <div className="w-16 h-16 bg-blue-100 rounded-full flex items-center justify-center mx-auto mb-4 text-blue-600">
                  {feature.icon}
                </div>
                <h3 className="text-xl font-semibold text-gray-900 mb-2">{feature.title}</h3>
                <p className="text-gray-600">{feature.desc}</p>
              </div>
            ))}
          </div>
        </div>
      </main>

      <footer className="bg-gray-800 text-white py-8 mt-20">
        <div className="max-w-7xl mx-auto px-4 text-center">
          <p>Â© 2023 ODSM. On-Demand Service Management Platform.</p>
        </div>
      </footer>
    </div>
  );
};

// --- AUTHENTICATION VIEWS (Login & Register) ---
const LoginView = ({ onLogin, onSwitchToRegister, onBackToHome }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    onLogin(email, password);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-indigo-100 p-4">
      <div className="bg-white p-8 rounded-2xl shadow-xl w-full max-w-md">
        <button onClick={onBackToHome} className="flex items-center text-blue-600 mb-6 hover:text-blue-800">
          <ChevronRight className="w-4 h-4 rotate-180 mr-1" /> Back to Home
        </button>
        <div className="text-center mb-8">
          <div className="mx-auto bg-gradient-to-r from-blue-500 to-indigo-600 w-16 h-16 rounded-full flex items-center justify-center mb-4">
            <Wrench className="w-8 h-8 text-white" />
          </div>
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Welcome Back</h1>
          <p className="text-gray-600">Sign in to your account</p>
        </div>
        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Email</label>
            <div className="relative">
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <User className="h-5 w-5 text-gray-400" />
              </div>
              <input
                type="email"
                required
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="w-full pl-10 px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                placeholder="Enter your email"
              />
            </div>
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Password</label>
            <div className="relative">
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <LockIcon />
              </div>
              <input
                type={showPassword ? 'text' : 'password'}
                required
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="w-full pl-10 px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                placeholder="Enter your password"
              />
              <button
                type="button"
                onClick={() => setShowPassword(!showPassword)}
                className="absolute right-3 top-3 text-gray-400 hover:text-gray-600"
              >
                {showPassword ? <EyeOff className="w-5 h-5" /> : <Eye className="w-5 h-5" />}
              </button>
            </div>
          </div>
          <button
            type="submit"
            className="w-full bg-gradient-to-r from-blue-600 to-indigo-700 text-white py-3 px-4 rounded-lg hover:from-blue-700 hover:to-indigo-800 font-medium shadow-md"
          >
            Sign In
          </button>
          <div className="text-center">
            <p className="text-gray-600">
              Don't have an account?{' '}
              <button
                type="button"
                onClick={onSwitchToRegister}
                className="text-blue-600 hover:text-blue-800 font-medium"
              >
                Register now
              </button>
            </p>
          </div>
        </form>
      </div>
    </div>
  );
};

const RegisterView = ({ onRegister, onSwitchToLogin, onBackToHome }) => {
  const [formData, setFormData] = useState({
    name: '', email: '', password: '', phone: '', address: '', role: 'user'
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onRegister(formData);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-indigo-100 p-4">
      <div className="bg-white p-8 rounded-2xl shadow-xl w-full max-w-md">
        <button onClick={onBackToHome} className="flex items-center text-blue-600 mb-6 hover:text-blue-800">
          <ChevronRight className="w-4 h-4 rotate-180 mr-1" /> Back to Home
        </button>
        <div className="text-center mb-8">
          <div className="mx-auto bg-gradient-to-r from-green-500 to-teal-600 w-16 h-16 rounded-full flex items-center justify-center mb-4">
            <UserPlus className="w-8 h-8 text-white" />
          </div>
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Create Account</h1>
          <p className="text-gray-600">Join our service platform</p>
        </div>
        <form onSubmit={handleSubmit} className="space-y-4">
          {['name', 'email', 'password', 'phone', 'address'].map(field => (
            <div key={field}>
              <label className="block text-sm font-medium text-gray-700 mb-2 capitalize">{field}</label>
              <input
                type={field === 'password' ? 'password' : field === 'phone' ? 'tel' : 'text'}
                name={field}
                required
                value={formData[field]}
                onChange={handleChange}
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                placeholder={`Enter your ${field}`}
              />
            </div>
          ))}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Account Type</label>
            <select
              name="role"
              value={formData.role}
              onChange={handleChange}
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            >
              <option value="user">Customer</option>
              <option value="provider">Service Provider</option>
            </select>
          </div>
          <button
            type="submit"
            className="w-full bg-gradient-to-r from-green-600 to-teal-700 text-white py-3 px-4 rounded-lg hover:from-green-700 hover:to-teal-800 font-medium shadow-md"
          >
            Create Account
          </button>
          <div className="text-center">
            <p className="text-gray-600">
              Already have an account?{' '}
              <button
                type="button"
                onClick={onSwitchToLogin}
                className="text-blue-600 hover:text-blue-800 font-medium"
              >
                Sign in
              </button>
            </p>
          </div>
        </form>
      </div>
    </div>
  );
};

// --- DASHBOARD COMPONENTS (Same as before, but simplified for brevity) ---
// [Include all your existing renderBookings, renderProviders, etc. logic here]
// For full code, see the complete implementation below ðŸ‘‡

// --- MAIN APP ---
const App = () => {
  const [authView, setAuthView] = useState(null); // null = public home, 'login', 'register'
  const [userRole, setUserRole] = useState('user');
  const [activeTab, setActiveTab] = useState('home');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);
  const [bookings, setBookings] = useState(INITIAL_BOOKINGS);
  const [showBookingModal, setShowBookingModal] = useState(false);
  const [selectedService, setSelectedService] = useState(null);
  const [bookingData, setBookingData] = useState({
    serviceId: '',
    date: '',
    time: '',
    address: '',
    notes: ''
  });

  // --- AUTH HANDLERS ---
  const handleLogin = (email, password) => {
    const user = PREDEFINED_USERS.find(u => u.email === email && u.password === password);
    if (user) {
      if (user.role === 'provider' && !user.is_verified) {
        alert('Your provider account is pending admin approval.');
        return;
      }
      setCurrentUser(user);
      setUserRole(user.role);
      setIsLoggedIn(true);
      setAuthView(null);
    } else {
      alert('Invalid email or password');
    }
  };

  const handleRegister = (data) => {
    if (PREDEFINED_USERS.some(u => u.email === data.email)) {
      alert('Email already registered');
      return;
    }
    alert('Registration successful! Please login.');
    setAuthView('login');
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setCurrentUser(null);
    setActiveTab('home');
  };

  // --- BOOKING HANDLERS ---
  const handleBookService = (service) => {
    setSelectedService(service);
    setBookingData({
      serviceId: service.id,
      date: '',
      time: '',
      address: currentUser?.address || '',
      notes: ''
    });
    setShowBookingModal(true);
  };

  const handleCreateBooking = (e) => {
    e.preventDefault();
    if (!bookingData.date || !bookingData.time) {
      alert('Please select both date and time.');
      return;
    }
    const newBooking = {
      id: bookings.length + 1,
      service_id: bookingData.serviceId,
      user_id: currentUser.id,
      provider_id: selectedService?.provider_id || null,
      date: bookingData.date,
      time: bookingData.time,
      address: bookingData.address,
      notes: bookingData.notes,
      status: 'Pending'
    };
    setBookings([...bookings, newBooking]);
    setShowBookingModal(false);
    setBookingData({ serviceId: '', date: '', time: '', address: '', notes: '' });
    alert('Booking created successfully!');
  };

  const updateBookingStatus = (id, status) => {
    setBookings(bookings.map(b => b.id === id ? { ...b, status } : b));
  };

  // --- STATUS HELPERS ---
  const getStatusColor = (status) => {
    switch (status) {
      case 'Pending': return 'bg-yellow-100 text-yellow-800';
      case 'Accepted': return 'bg-blue-100 text-blue-800';
      case 'In Progress': return 'bg-purple-100 text-purple-800';
      case 'Completed': return 'bg-green-100 text-green-800';
      case 'Cancelled': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'Pending': return <Clock className="w-4 h-4" />;
      case 'Accepted': return <UserCheck className="w-4 h-4" />;
      case 'In Progress': return <Play className="w-4 h-4" />;
      case 'Completed': return <CheckCircle className="w-4 h-4" />;
      case 'Cancelled': return <XCircle className="w-4 h-4" />;
      default: return <Clock className="w-4 h-4" />;
    }
  };

  // --- RENDER DASHBOARD VIEWS ---
  const renderDashboard = () => {
    const providers = PREDEFINED_USERS.filter(u => u.role === 'provider');
    const filteredBookings = userRole === 'user'
      ? bookings.filter(b => b.user_id === currentUser?.id)
      : bookings.filter(b => b.provider_id === currentUser?.id);

    const navigation = [
      { id: 'home', label: 'Home', icon: Home },
      { id: 'bookings', label: 'Bookings', icon: Calendar },
      { id: 'providers', label: 'Providers', icon: Users },
      ...(userRole === 'admin' ? [{ id: 'admin', label: 'Admin', icon: Settings }] : []),
      { id: 'profile', label: 'Profile', icon: User }
    ];

    return (
      <div className="min-h-screen bg-gradient-to-b from-gray-50 to-gray-100">
        {/* Header */}
        <header className="bg-white shadow-sm border-b border-gray-200">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <div className="flex justify-between items-center h-16">
              <div className="flex items-center">
                <div className="bg-gradient-to-r from-blue-500 to-indigo-600 w-10 h-10 rounded-lg flex items-center justify-center mr-3">
                  <Wrench className="w-6 h-6 text-white" />
                </div>
                <h1 className="text-2xl font-bold bg-gradient-to-r from-blue-600 to-indigo-700 bg-clip-text text-transparent">ODSM</h1>
              </div>
              <div className="flex items-center space-x-4">
                <span className="px-3 py-1 bg-gradient-to-r from-blue-100 to-indigo-100 text-blue-800 rounded-full text-sm font-medium capitalize">
                  {userRole}
                </span>
                <button
                  onClick={handleLogout}
                  className="flex items-center px-4 py-2 bg-gray-100 hover:bg-gray-200 rounded-lg text-gray-700"
                >
                  <LogIn className="w-4 h-4 mr-1" /> Logout
                </button>
              </div>
            </div>
          </div>
        </header>

        {/* Navigation */}
        <nav className="bg-white shadow-sm border-b border-gray-200">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <div className="flex space-x-1">
              {navigation.map(item => {
                const Icon = item.icon;
                return (
                  <button
                    key={item.id}
                    onClick={() => setActiveTab(item.id)}
                    className={`flex items-center px-4 py-3 font-medium text-sm transition-all duration-300 rounded-t-lg ${
                      activeTab === item.id
                        ? 'text-blue-600 bg-blue-50 border-b-2 border-blue-600'
                        : 'text-gray-600 hover:text-gray-900 hover:bg-gray-50'
                    }`}
                  >
                    <Icon className="w-4 h-4 mr-2" /> {item.label}
                  </button>
                );
              })}
            </div>
          </div>
        </nav>

        {/* Main Content */}
        <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          {activeTab === 'home' && (
            <div className="space-y-8">
              <div className="text-center mb-10">
                <h1 className="text-4xl md:text-5xl font-bold text-gray-900 mb-4">Welcome Back, {currentUser?.name}!</h1>
                <p className="text-xl text-gray-600 max-w-2xl mx-auto">
                  Book your next service or manage existing requests.
                </p>
              </div>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {SERVICES.map(service => (
                  <div key={service.id} className="bg-white rounded-2xl shadow-lg p-6 hover:shadow-xl transition-all border border-gray-100">
                    <div className="flex justify-between items-start mb-4">
                      <div className="text-4xl">ðŸ”§</div>
                      <span className="px-2 py-1 bg-blue-100 text-blue-800 text-xs font-semibold rounded-full">
                        {service.category?.name || 'Service'}
                      </span>
                    </div>
                    <h3 className="text-xl font-bold text-gray-900 mb-2">{service.name}</h3>
                    <p className="text-gray-600 mb-4">
                      Starting from <span className="font-semibold">â‚¹{service.base_price_min}</span>
                    </p>
                    <button
                      onClick={() => handleBookService(service)}
                      className="w-full bg-gradient-to-r from-blue-600 to-indigo-700 text-white py-2.5 px-4 rounded-lg hover:from-blue-700 hover:to-indigo-800 font-medium shadow-md"
                    >
                      Book Now
                    </button>
                  </div>
                ))}
              </div>
            </div>
          )}

          {activeTab === 'bookings' && (
            <div className="space-y-6">
              <div className="flex justify-between items-center">
                <h2 className="text-2xl font-bold text-gray-900">
                  {userRole === 'user' ? 'My Bookings' : 'My Service Requests'}
                </h2>
                {userRole === 'user' && (
                  <button
                    onClick={() => {
                      setSelectedService(null);
                      setShowBookingModal(true);
                    }}
                    className="bg-gradient-to-r from-blue-600 to-indigo-700 text-white px-4 py-2 rounded-lg hover:from-blue-700 hover:to-indigo-800 flex items-center shadow-md"
                  >
                    <Plus className="w-4 h-4 mr-1" /> New Booking
                  </button>
                )}
              </div>
              {filteredBookings.length === 0 ? (
                <div className="bg-white rounded-2xl shadow-lg p-12 text-center border border-gray-100">
                  <div className="mx-auto bg-gray-100 w-20 h-20 rounded-full flex items-center justify-center mb-6">
                    <Calendar className="w-10 h-10 text-gray-400" />
                  </div>
                  <h3 className="text-xl font-bold text-gray-900 mb-2">No bookings yet</h3>
                  <p className="text-gray-600 mb-6 max-w-md mx-auto">
                    {userRole === 'user'
                      ? 'Start by booking a service from our available professionals'
                      : 'You have no service requests at the moment'}
                  </p>
                  {userRole === 'user' && (
                    <button
                      onClick={() => setActiveTab('home')}
                      className="bg-gradient-to-r from-blue-600 to-indigo-700 text-white px-6 py-3 rounded-lg hover:from-blue-700 hover:to-indigo-800 font-medium shadow-md"
                    >
                      Browse Services
                    </button>
                  )}
                </div>
              ) : (
                <div className="space-y-4">
                  {filteredBookings.map(booking => {
                    const service = SERVICES.find(s => s.id === booking.service_id);
                    const provider = providers.find(p => p.id === booking.provider_id);
                    const user = PREDEFINED_USERS.find(u => u.id === booking.user_id);
                    return (
                      <div key={booking.id} className="bg-white rounded-2xl shadow-md p-6 border border-gray-100">
                        <div className="flex justify-between items-start mb-4">
                          <div>
                            <h3 className="text-xl font-bold text-gray-900">{service?.name || 'Service'}</h3>
                            <p className="text-gray-600">
                              {userRole === 'user'
                                ? `Provider: ${provider?.name || 'N/A'}`
                                : `Customer: ${user?.name || 'N/A'}`
                              }
                            </p>
                          </div>
                          <span className={`px-3 py-1 rounded-full text-sm font-medium flex items-center ${getStatusColor(booking.status)}`}>
                            {getStatusIcon(booking.status)}
                            <span className="ml-1">{booking.status}</span>
                          </span>
                        </div>
                        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
                          <div className="flex items-center text-gray-600"><Calendar className="w-4 h-4 mr-2 text-blue-500" />{booking.date}</div>
                          <div className="flex items-center text-gray-600"><Clock className="w-4 h-4 mr-2 text-purple-500" />{booking.time}</div>
                          <div className="flex items-center text-gray-600"><MapPin className="w-4 h-4 mr-2 text-red-500" />{booking.address}</div>
                        </div>
                        {booking.notes && (
                          <div className="mb-4 p-3 bg-gray-50 rounded-lg border border-gray-200">
                            <p className="text-sm text-gray-600"><strong>Notes:</strong> {booking.notes}</p>
                          </div>
                        )}
                        <div className="flex flex-wrap gap-2">
                          {userRole === 'user' && booking.status === 'Pending' && (
                            <>
                              <button onClick={() => updateBookingStatus(booking.id, 'Cancelled')} className="px-4 py-2 bg-gradient-to-r from-red-600 to-red-700 text-white rounded-lg hover:from-red-700 hover:to-red-800 shadow-md">
                                Cancel
                              </button>
                              <button className="px-4 py-2 bg-gradient-to-r from-gray-600 to-gray-700 text-white rounded-lg hover:from-gray-700 hover:to-gray-800 shadow-md">
                                Reschedule
                              </button>
                            </>
                          )}
                          {userRole === 'provider' && booking.status === 'Pending' && (
                            <>
                              <button onClick={() => updateBookingStatus(booking.id, 'Accepted')} className="px-4 py-2 bg-gradient-to-r from-green-600 to-teal-700 text-white rounded-lg hover:from-green-700 hover:to-teal-800 shadow-md">
                                Accept
                              </button>
                              <button onClick={() => updateBookingStatus(booking.id, 'Cancelled')} className="px-4 py-2 bg-gradient-to-r from-red-600 to-red-700 text-white rounded-lg hover:from-red-700 hover:to-red-800 shadow-md">
                                Reject
                              </button>
                            </>
                          )}
                          {userRole === 'provider' && booking.status === 'Accepted' && (
                            <button onClick={() => updateBookingStatus(booking.id, 'In Progress')} className="px-4 py-2 bg-gradient-to-r from-purple-600 to-indigo-700 text-white rounded-lg hover:from-purple-700 hover:to-indigo-800 shadow-md">
                              Start Service
                            </button>
                          )}
                          {userRole === 'provider' && booking.status === 'In Progress' && (
                            <button onClick={() => updateBookingStatus(booking.id, 'Completed')} className="px-4 py-2 bg-gradient-to-r from-green-600 to-teal-700 text-white rounded-lg hover:from-green-700 hover:to-teal-800 shadow-md">
                              Mark Complete
                            </button>
                          )}
                          {booking.status === 'Completed' && (
                            <button className="px-4 py-2 bg-gradient-to-r from-yellow-500 to-amber-600 text-white rounded-lg hover:from-yellow-600 hover:to-amber-700 shadow-md">
                              Rate Service
                            </button>
                          )}
                        </div>
                      </div>
                    );
                  })}
                </div>
              )}
            </div>
          )}

          {activeTab === 'providers' && (
            <div className="space-y-6">
              <h2 className="text-2xl font-bold text-gray-900">Service Providers</h2>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                {providers.map(provider => {
                  const providerServices = SERVICES.filter(s => s.provider_id === provider.id);
                  return (
                    <div key={provider.id} className="bg-white rounded-2xl shadow-lg p-6 border border-gray-100">
                      <div className="flex justify-between items-start">
                        <div>
                          <div className="flex items-center mb-3">
                            <div className="w-12 h-12 bg-gradient-to-r from-blue-500 to-indigo-600 rounded-full flex items-center justify-center text-white font-bold text-lg mr-3">
                              {provider.name.split(' ').map(n => n[0]).join('')}
                            </div>
                            <div>
                              <h3 className="text-xl font-bold text-gray-900">{provider.name}</h3>
                              <p className="text-gray-600 text-sm">{provider.email}</p>
                            </div>
                          </div>
                          <p className="text-gray-600 mb-2">
                            {providerServices.length > 0 ? providerServices.map(s => s.name).join(', ') : 'General Service Provider'}
                          </p>
                          <div className="flex items-center mt-2">
                            <Star className="w-4 h-4 text-yellow-400 fill-current" />
                            <span className="ml-1 text-gray-700 font-medium">4.5</span>
                            <span className="text-gray-500 ml-2">({providerServices.length} services)</span>
                          </div>
                        </div>
                        <span className="px-3 py-1 rounded-full text-sm font-medium bg-green-100 text-green-800">Available</span>
                      </div>
                      <div className="mt-6 flex gap-3">
                        <button className="flex-1 flex items-center justify-center px-4 py-2.5 bg-gradient-to-r from-blue-600 to-indigo-700 text-white rounded-lg hover:from-blue-700 hover:to-indigo-800 shadow-md">
                          <Phone className="w-4 h-4 mr-2" /> Contact
                        </button>
                        <button className="flex-1 px-4 py-2.5 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50">
                          View Profile
                        </button>
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>
          )}

          {activeTab === 'admin' && (
            <div className="space-y-8">
              <h2 className="text-2xl font-bold text-gray-900">Admin Dashboard</h2>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                <div className="bg-white rounded-2xl shadow-lg p-6 border border-gray-100">
                  <div className="flex items-center">
                    <div className="p-3 bg-blue-100 rounded-xl mr-4">
                      <Users className="w-8 h-8 text-blue-600" />
                    </div>
                    <div>
                      <p className="text-2xl font-bold text-gray-900">{PREDEFINED_USERS.filter(u => u.role === 'user').length}</p>
                      <p className="text-gray-600">Customers</p>
                    </div>
                  </div>
                </div>
                <div className="bg-white rounded-2xl shadow-lg p-6 border border-gray-100">
                  <div className="flex items-center">
                    <div className="p-3 bg-green-100 rounded-xl mr-4">
                      <Wrench className="w-8 h-8 text-green-600" />
                    </div>
                    <div>
                      <p className="text-2xl font-bold text-gray-900">{providers.length}</p>
                      <p className="text-gray-600">Providers</p>
                    </div>
                  </div>
                </div>
                <div className="bg-white rounded-2xl shadow-lg p-6 border border-gray-100">
                  <div className="flex items-center">
                    <div className="p-3 bg-purple-100 rounded-xl mr-4">
                      <CheckCircle className="w-8 h-8 text-purple-600" />
                    </div>
                    <div>
                      <p className="text-2xl font-bold text-gray-900">
                        {bookings.length ? Math.round((bookings.filter(b => b.status === 'Completed').length / bookings.length) * 100) : 0}%
                      </p>
                      <p className="text-gray-600">Completion Rate</p>
                    </div>
                  </div>
                </div>
                <div className="bg-white rounded-2xl shadow-lg p-6 border border-gray-100">
                  <div className="flex items-center">
                    <div className="p-3 bg-yellow-100 rounded-xl mr-4">
                      <DollarSign className="w-8 h-8 text-yellow-600" />
                    </div>
                    <div>
                      <p className="text-2xl font-bold text-gray-900">{bookings.length}</p>
                      <p className="text-gray-600">Total Bookings</p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}

          {activeTab === 'profile' && currentUser && (
            <div className="bg-white rounded-2xl shadow-lg p-8 max-w-2xl mx-auto border border-gray-100">
              <div className="flex items-center mb-8">
                <div className="w-24 h-24 bg-gradient-to-r from-blue-500 to-indigo-600 rounded-full flex items-center justify-center text-white text-3xl font-bold mr-6">
                  {currentUser.name.split(' ').map(n => n[0]).join('')}
                </div>
                <div>
                  <h2 className="text-2xl font-bold text-gray-900">{currentUser.name}</h2>
                  <p className="text-gray-600 text-lg">{currentUser.email}</p>
                  <p className="text-gray-600">{currentUser.phone}</p>
                  <span className={`inline-block px-3 py-1 mt-2 rounded-full text-sm font-medium ${
                    currentUser.role === 'admin' ? 'bg-purple-100 text-purple-800' :
                    currentUser.role === 'provider' ? 'bg-green-100 text-green-800' :
                    'bg-blue-100 text-blue-800'
                  }`}>
                    {currentUser.role}
                  </span>
                </div>
              </div>
              <div className="space-y-5">
                {['name', 'email', 'phone', 'address'].map(field => (
                  <div key={field}>
                    <label className="block text-sm font-medium text-gray-700 mb-2 capitalize">{field}</label>
                    <input
                      type={field === 'phone' ? 'tel' : 'text'}
                      className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                      defaultValue={currentUser[field]}
                    />
                  </div>
                ))}
                <button className="w-full bg-gradient-to-r from-blue-600 to-indigo-700 text-white py-3 px-4 rounded-lg hover:from-blue-700 hover:to-indigo-800 font-medium shadow-md">
                  Save Changes
                </button>
              </div>
            </div>
          )}
        </main>

        {/* Booking Modal */}
        {showBookingModal && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
            <div className="bg-white rounded-2xl shadow-2xl w-full max-w-md p-6">
              <div className="flex justify-between items-center mb-4">
                <h3 className="text-xl font-bold text-gray-900">
                  Book {selectedService ? selectedService.name : 'Service'}
                </h3>
                <button onClick={() => setShowBookingModal(false)} className="text-gray-400 hover:text-gray-600">
                  <XCircle className="w-6 h-6" />
                </button>
              </div>
              <form onSubmit={handleCreateBooking} className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Service Date</label>
                  <input
                    type="date"
                    required
                    min={new Date().toISOString().split('T')[0]}
                    value={bookingData.date}
                    onChange={(e) => setBookingData({ ...bookingData, date: e.target.value })}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Service Time</label>
                  <input
                    type="time"
                    required
                    value={bookingData.time}
                    onChange={(e) => setBookingData({ ...bookingData, time: e.target.value })}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Service Address</label>
                  <textarea
                    required
                    value={bookingData.address}
                    onChange={(e) => setBookingData({ ...bookingData, address: e.target.value })}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    placeholder="Enter service address"
                    rows="2"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">Additional Notes (Optional)</label>
                  <textarea
                    value={bookingData.notes}
                    onChange={(e) => setBookingData({ ...bookingData, notes: e.target.value })}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    placeholder="Any specific requirements..."
                    rows="3"
                  />
                </div>
                <div className="flex gap-3 pt-4">
                  <button type="button" onClick={() => setShowBookingModal(false)} className="flex-1 px-4 py-2 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50">
                    Cancel
                  </button>
                  <button type="submit" className="flex-1 bg-gradient-to-r from-blue-600 to-indigo-700 text-white py-2 px-4 rounded-lg hover:from-blue-700 hover:to-indigo-800 shadow-md">
                    Confirm Booking
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}
      </div>
    );
  };

  // --- RENDER BASED ON STATE ---
  if (authView === 'login') {
    return <LoginView 
      onLogin={handleLogin} 
      onSwitchToRegister={() => setAuthView('register')} 
      onBackToHome={() => setAuthView(null)} 
    />;
  }

  if (authView === 'register') {
    return <RegisterView 
      onRegister={handleRegister} 
      onSwitchToLogin={() => setAuthView('login')} 
      onBackToHome={() => setAuthView(null)} 
    />;
  }

  if (isLoggedIn) {
    return renderDashboard();
  }

  return <PublicHomePage 
    onLoginClick={() => setAuthView('login')} 
    onRegisterClick={() => setAuthView('register')} 
  />;
};

export default App;
