import React, { useState } from 'react';
import {
  Calendar, Clock, Star, MapPin, Phone, User, Settings, Home,
  Users, Wrench, DollarSign, CheckCircle, XCircle, Play, UserCheck,
  UserX, LogIn, UserPlus, Eye, EyeOff, Plus
} from 'lucide-react';

// --- PREDEFINED DATA ---
const PREDEFINED_USERS = [
  { id: 1, name: "John Doe", email: "john@example.com", password: "password123", phone: "+1234567890", address: "123 Main St, Cityville", role: "user", is_verified: true },
  { id: 2, name: "Jane Smith", email: "jane@example.com", password: "password123", phone: "+0987654321", address: "456 Oak Ave, Townsville", role: "provider", is_verified: true, services: [1, 2] },
  { id: 3, name: "Admin User", email: "admin@example.com", password: "admin123", phone: "+1122334455", address: "789 Admin Blvd, Metropolis", role: "admin", is_verified: true }
];

const SERVICES = [
  { id: 1, name: "Plumbing Service", category: { name: "Home Maintenance" }, base_price_min: 50, base_price_max: 150, provider_id: 2 },
  { id: 2, name: "Electrical Repair", category: { name: "Home Maintenance" }, base_price_min: 60, base_price_max: 200, provider_id: 2 },
  { id: 3, name: "Cleaning Service", category: { name: "Home Care" }, base_price_min: 40, base_price_max: 120, provider_id: null }
];

const INITIAL_BOOKINGS = [
  { id: 1, service_id: 1, user_id: 1, provider_id: 2, date: "2023-06-15", time: "10:00", address: "123 Main St, Cityville", notes: "Leaky faucet in kitchen", status: "Completed" },
  { id: 2, service_id: 2, user_id: 1, provider_id: 2, date: "2023-06-20", time: "14:00", address: "123 Main St, Cityville", notes: "Replace light fixture", status: "Pending" }
];

// --- HELPER COMPONENTS ---
const LockIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
    <path fillRule="evenodd" d="M5 9V7a5 5 0 0110 0v2a2 2 0 012 2v5a2 2 0 01-2 2H5a2 2 0 01-2-2v-5a2 2 0 012-2zm8-2v2H7V7a3 3 0 016 0z" clipRule="evenodd" />
  </svg>
);

const StatusBadge = ({ status }) => {
  const getColor = (s) => {
    switch (s) {
      case 'Pending': return 'bg-yellow-100 text-yellow-800';
      case 'Accepted': return 'bg-blue-100 text-blue-800';
      case 'In Progress': return 'bg-purple-100 text-purple-800';
      case 'Completed': return 'bg-green-100 text-green-800';
      case 'Cancelled': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getIcon = (s) => {
    switch (s) {
      case 'Pending': return <Clock className="w-4 h-4" />;
      case 'Accepted': return <UserCheck className="w-4 h-4" />;
      case 'In Progress': return <Play className="w-4 h-4" />;
      case 'Completed': return <CheckCircle className="w-4 h-4" />;
      case 'Cancelled': return <XCircle className="w-4 h-4" />;
      default: return <Clock className="w-4 h-4" />;
    }
  };

  return (
    <span className={`px-3 py-1 rounded-full text-sm font-medium flex items-center ${getColor(status)}`}>
      {getIcon(status)}
      <span className="ml-1">{status}</span>
    </span>
  );
};

// --- MAIN COMPONENTS ---
const LoginView = ({ onLogin, onSwitchToRegister }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    onLogin(email, password);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-indigo-100 p-4">
      <div className="bg-white p-8 rounded-2xl shadow-xl w-full max-w-md transition-all duration-300 hover:shadow-2xl">
        <div className="text-center mb-8">
          <div className="mx-auto bg-gradient-to-r from-blue-500 to-indigo-600 w-16 h-16 rounded-full flex items-center justify-center mb-4">
            <Wrench className="w-8 h-8 text-white" />
          </div>
          <h1 className="text-3xl font-bold text-gray-900 mb-2">ODSM</h1>
          <p className="text-gray-600">On-Demand Service Management</p>
        </div>
        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Email</label>
            <div className="relative">
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <User className="h-5 w-5 text-gray-400" />
              </div>
              <input
                type="email"
                required
                value={email}
                onChange={(e) => setEmail(e.target.value)}
