const BookingCard = ({ booking, services, users, providers, userRole, onUpdateStatus }) => {
  const service = services.find(s => s.id === booking.service_id);
  const provider = providers.find(p => p.id === booking.provider_id);
  const user = users.find(u => u.id === booking.user_id);

  return (
    <div className="bg-white rounded-2xl shadow-md p-6 border border-gray-100 transition-all duration-300 hover:shadow-lg">
      <div className="flex justify-between items-start mb-4">
        <div>
          <h3 className="text-xl font-bold text-gray-900">{service?.name || 'Service'}</h3>
          <p className="text-gray-600">
            {userRole === 'user'
              ? `Provider: ${provider?.name || 'N/A'}`
              : `Customer: ${user?.name || 'N/A'}`}
          </p>
        </div>
        <StatusBadge status={booking.status} />
      </div>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
        <div className="flex items-center text-gray-600"><Calendar className="w-4 h-4 mr-2 text-blue-500" />{booking.date}</div>
        <div className="flex items-center text-gray-600"><Clock className="w-4 h-4 mr-2 text-purple-500" />{booking.time}</div>
        <div className="flex items-center text-gray-600"><MapPin className="w-4 h-4 mr-2 text-red-500" />{booking.address}</div>
      </div>
      {booking.notes && (
        <div className="mb-4 p-3 bg-gray-50 rounded-lg border border-gray-200">
          <p className="text-sm text-gray-600"><strong>Notes:</strong> {booking.notes}</p>
        </div>
      )}
      <div className="flex flex-wrap gap-2">
        {userRole === 'user' && booking.status === 'Pending' && (
          <>
            <button onClick={() => onUpdateStatus(booking.id, 'Cancelled')} className="px-4 py-2 bg-gradient-to-r from-red-600 to-red-700 text-white rounded-lg hover:from-red-700 hover:to-red-800 shadow-md">
              Cancel
            </button>
            <button className="px-4 py-2 bg-gradient-to-r from-gray-600 to-gray-700 text-white rounded-lg hover:from-gray-700 hover:to-gray-800 shadow-md">
              Reschedule
            </button>
          </>
        )}
        {userRole === 'provider' && booking.status === 'Pending' && (
          <>
            <button onClick={() => onUpdateStatus(booking.id, 'Accepted')} className="px-4 py-2 bg-gradient-to-r from-green-600 to-teal-700 text-white rounded-lg hover:from-green-700 hover:to-teal-800 shadow-md">
              Accept
            </button>
            <button onClick={() => onUpdateStatus(booking.id, 'Cancelled')} className="px-4 py-2 bg-gradient-to-r from-red-600 to-red-700 text-white rounded-lg hover:from-red-700 hover:to-red-800 shadow-md">
              Reject
            </button>
          </>
        )}
        {userRole === 'provider' && booking.status === 'Accepted' && (
          <button onClick={() => onUpdateStatus(booking.id, 'In Progress')} className="px-4 py-2 bg-gradient-to-r from-purple-600 to-indigo-700 text-white rounded-lg hover:from-purple-700 hover:to-indigo-800 shadow-md">
            Start Service
          </button>
        )}
        {userRole === 'provider' && booking.status === 'In Progress' && (
          <button onClick={() => onUpdateStatus(booking.id, 'Completed')} className="px-4 py-2 bg-gradient-to-r from-green-600 to-teal-700 text-white rounded-lg hover:from-green-700 hover:to-teal-800 shadow-md">
            Mark Complete
          </button>
        )}
        {booking.status === 'Completed' && (
          <button className="px-4 py-2 bg-gradient-to-r from-yellow-500 to-amber-600 text-white rounded-lg hover:from-yellow-600 hover:to-amber-700 shadow-md">
            Rate Service
          </button>
        )}
      </div>
    </div>
  );
};

