import React, { useState } from 'react';
import {
  AlertTriangle, Calendar, Clock, Star, MapPin, Phone, User, Settings, Home,
  Users, Wrench, DollarSign, CheckCircle, XCircle, Play, Pause, UserCheck,
  UserX, LogIn, UserPlus, Eye, EyeOff, Plus, Filter, ChevronRight
} from 'lucide-react';

// Predefined users data
const PREDEFINED_USERS = [
  {
    id: 1,
    name: "John Doe",
    email: "john@example.com",
    password: "password123",
    phone: "+1234567890",
    address: "123 Main St, Cityville",
    role: "user",
    is_verified: true
  },
  {
    id: 2,
    name: "Jane Smith",
    email: "jane@example.com",
    password: "password123",
    phone: "+0987654321",
    address: "456 Oak Ave, Townsville",
    role: "provider",
    is_verified: true,
    services: [1, 2]
  },
  {
    id: 3,
    name: "Admin User",
    email: "admin@example.com",
    password: "admin123",
    phone: "+1122334455",
    address: "789 Admin Blvd, Metropolis",
    role: "admin",
    is_verified: true
  },
  {
    id: 4,
    name: "Pending Provider",
    email: "pending@example.com",
    password: "pending123",
    phone: "+5555555555",
    address: "101 Test St",
    role: "provider",
    is_verified: false
  }
];

// Predefined services
const SERVICES = [
  {
    id: 1,
    name: "Plumbing Service",
    category: { name: "Home Maintenance" },
    base_price_min: 50,
    base_price_max: 150,
    provider_id: 2
  },
  {
    id: 2,
    name: "Electrical Repair",
    category: { name: "Home Maintenance" },
    base_price_min: 60,
    base_price_max: 200,
    provider_id: 2
  },
  {
    id: 3,
    name: "Cleaning Service",
    category: { name: "Home Care" },
    base_price_min: 40,
    base_price_max: 120,
    provider_id: null
  }
];

// Predefined bookings
const INITIAL_BOOKINGS = [
  {
    id: 1,
    service_id: 1,
    user_id: 1,
    provider_id: 2,
    date: "2023-06-15",
    time: "10:00",
    address: "123 Main St, Cityville",
    notes: "Leaky faucet in kitchen",
    status: "Completed"
  },
  {
    id: 2,
    service_id: 2,
    user_id: 1,
    provider_id: 2,
    date: "2023-06-20",
    time: "14:00",
    address: "123 Main St, Cityville",
    notes: "Replace light fixture in living room",
    status: "Pending"
  }
];

const LockIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
    <path fillRule="evenodd" d="M5 9V7a5 5 0 0110 0v2a2 2 0 012 2v5a2 2 0 01-2 2H5a2 2 0 01-2-2v-5a2 2 0 012-2zm8-2v2H7V7a3 3 0 016 0z" clipRule="evenodd" />
  </svg>
);

// --- PUBLIC HOME PAGE ---
const PublicHome = ({ onLogin, onRegister }) => {
  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-50 to-gray-100">
      {/* Header */}
      <header className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4 flex justify-between items-center">
          <div className="flex items-center">
            <div className="bg-gradient-to-r from-blue-500 to-indigo-600 w-10 h-10 rounded-lg flex items-center justify-center mr-3">
              <Wrench className="w-6 h-6 text-white" />
            </div>
            <h1 className="text-2xl font-bold bg-gradient-to-r from-blue-600 to-indigo-700 bg-clip-text text-transparent">ODSM</h1>
          </div>
          <div className="flex space-x-3">
            <button
              onClick={onRegister}
              className="px-4 py-2 text-blue-600 font-medium hover:text-blue-800 transition-colors"
            >
              Register
            </button>
            <button
