};

const App = () => {
  const [currentView, setCurrentView] = useState('public'); // 'public', 'login', 'register'
  const [userRole, setUserRole] = useState('user');
  const [activeTab, setActiveTab] = useState('home');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [showBookingModal, setShowBookingModal] = useState(false);
  const [selectedService, setSelectedService] = useState(null);
  const [currentUser, setCurrentUser] = useState(null);

  // Login/Register state
  const [loginData, setLoginData] = useState({ email: '', password: '' });
  const [registerData, setRegisterData] = useState({
    name: '',
    email: '',
    password: '',
    phone: '',
    address: '',
    role: 'user'
  });

  // Booking form state
  const [bookingData, setBookingData] = useState({
    serviceId: '',
    date: '',
    time: '',
    address: '',
    notes: ''
  });

  // State for data
  const [bookings, setBookings] = useState(INITIAL_BOOKINGS);
  const [services, setServices] = useState(SERVICES);
  const [providers, setProviders] = useState(PREDEFINED_USERS.filter(u => u.role === 'provider' && u.is_verified));

  // Login function
  const handleLogin = (e) => {
    e.preventDefault();
    const user = PREDEFINED_USERS.find(
      u => u.email === loginData.email && u.password === loginData.password
    );
    if (user) {
      if (user.role === 'provider' && !user.is_verified) {
        alert('Your provider account is pending admin approval.');
        return;
      }
      setCurrentUser(user);
      setUserRole(user.role);
      setIsLoggedIn(true);
    } else {
      alert('Invalid email or password');
    }
  };

  // Register function
  const handleRegister = (e) => {
    e.preventDefault();
    const existingUser = PREDEFINED_USERS.find(u => u.email === registerData.email);
    if (existingUser) {
      alert('Email already registered');
      return;
    }
    const newUser = {
      id: PREDEFINED_USERS.length + 1,
      ...registerData,
      is_verified: registerData.role === 'user' ? true : false
    };
    // In a real app, we'd add to database
    // For demo, just show success message
    alert(registerData.role === 'user' 
      ? 'Registration successful! Please login.' 
      : 'Registration successful! Your account is pending admin approval.');
    setCurrentView('login');
  };

  // Logout function
  const handleLogout = () => {
    setIsLoggedIn(false);
    setCurrentUser(null);
    setLoginData({ email: '', password: '' });
    setActiveTab('home');
  };

  // Booking functions
  const handleBookService = (service) => {
    setSelectedService(service);
    setBookingData({
      serviceId: service.id,
      date: '',
      time: '',
      address: currentUser?.address || '',
      notes: ''
    });
    setShowBookingModal(true);
  };

  const handleCreateBooking = (e) => {
    e.preventDefault();
    if (!bookingData.date || !bookingData.time) {
      alert('Please select both date and time.');
      return;
    }
    const newBooking = {
      id: bookings.length + 1,
      service_id: bookingData.serviceId,
      user_id: currentUser.id,
      provider_id: selectedService.provider_id,
      date: bookingData.date,
      time: bookingData.time,
      address: bookingData.address,
      notes: bookingData.notes,
      status: 'Pending'
    };
    setBookings([...bookings, newBooking]);
    setShowBookingModal(false);
    setBookingData({ serviceId: '', date: '', time: '', address: '', notes: '' });
    alert('Booking created successfully!');
  };

  const updateBookingStatus = (bookingId, newStatus) => {
    setBookings(bookings.map(booking =>
      booking.id === bookingId ? { ...booking, status: newStatus } : booking
    ));
  };

  // UI helpers
  const getStatusColor = (status) => {
    switch (status) {
      case 'Pending': return 'bg-yellow-100 text-yellow-800';
      case 'Accepted': return 'bg-blue-100 text-blue-800';
      case 'In Progress': return 'bg-purple-100 text-purple-800';
      case 'Completed': return 'bg-green-100 text-green-800';
      case 'Cancelled': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'Pending': return <Clock className="w-4 h-4" />;
      case 'Accepted': return <UserCheck className="w-4 h-4" />;
      case 'In Progress': return <Play className="w-4 h-4" />;
      case 'Completed': return <CheckCircle className="w-4 h-4" />;
      case 'Cancelled': return <XCircle className="w-4 h-4" />;
      default: return <Clock className="w-4 h-4" />;
    }
  };

  // === RENDER FUNCTIONS ===
  const renderLogin = () => (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-indigo-100 p-4">
