import React, { useState } from 'react';
import {
  Calendar, Clock, Star, MapPin, Phone, User, Settings, Home,
  Users, Wrench, DollarSign, CheckCircle, XCircle, Play, UserCheck,
  UserX, LogIn, UserPlus, Eye, EyeOff, Plus, Trash2, Shield, AlertTriangle
} from 'lucide-react';

// --- PREDEFINED DATA ---
const INITIAL_USERS = [
  { id: 1, name: "John Doe", email: "john@example.com", password: "password123", phone: "+1234567890", address: "123 Main St", role: "user", is_verified: true },
  { id: 2, name: "Jane Smith", email: "jane@example.com", password: "password123", phone: "+0987654321", address: "456 Oak Ave", role: "provider", is_verified: true, services: [1, 2] },
  { id: 3, name: "Admin User", email: "admin@example.com", password: "admin123", phone: "+1122334455", address: "789 Admin Blvd", role: "admin", is_verified: true },
  { id: 4, name: "Pending Provider", email: "pending@example.com", password: "pending123", phone: "+5555555555", address: "101 Test St", role: "provider", is_verified: false }
];

const INITIAL_SERVICES = [
  { id: 1, name: "Plumbing Service", category: "Home Maintenance", base_price_min: 50, base_price_max: 150, provider_id: 2 },
  { id: 2, name: "Electrical Repair", category: "Home Maintenance", base_price_min: 60, base_price_max: 200, provider_id: 2 },
  { id: 3, name: "Cleaning Service", category: "Home Care", base_price_min: 40, base_price_max: 120, provider_id: null }
];

const INITIAL_BOOKINGS = [
  { id: 1, service_id: 1, user_id: 1, provider_id: 2, date: "2023-06-15", time: "10:00", address: "123 Main St", notes: "Leaky faucet", status: "Completed" },
  { id: 2, service_id: 2, user_id: 1, provider_id: 2, date: "2023-06-20", time: "14:00", address: "123 Main St", notes: "Replace light fixture", status: "Pending" }
];

// --- CUSTOM ICON ---
const LockIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
    <path fillRule="evenodd" d="M5 9V7a5 5 0 0110 0v2a2 2 0 012 2v5a2 2 0 01-2 2H5a2 2 0 01-2-2v-5a2 2 0 012-2zm8-2v2H7V7a3 3 0 016 0z" clipRule="evenodd" />
  </svg>
);

// --- MAIN APP ---
const App = () => {
  const [users, setUsers] = useState(INITIAL_USERS);
  const [services, setServices] = useState(INITIAL_SERVICES);
  const [bookings, setBookings] = useState(INITIAL_BOOKINGS);

  const [currentView, setCurrentView] = useState('login');
  const [userRole, setUserRole] = useState('user');
  const [activeTab, setActiveTab] = useState('home');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);

  const [loginData, setLoginData] = useState({ email: '', password: '' });
  const [registerData, setRegisterData] = useState({ name: '', email: '', password: '', phone: '', address: '', role: 'user' });

  const [showBookingModal, setShowBookingModal] = useState(false);
  const [selectedService, setSelectedService] = useState(null);
  const [bookingData, setBookingData] = useState({ serviceId: '', date: '', time: '', address: '', notes: '' });

  const [showAddServiceModal, setShowAddServiceModal] = useState(false);
  const [newService, setNewService] = useState({ name: '', category: '', base_price_min: '', base_price_max: '' });

  // --- LOGIN ---
  const handleLogin = (e) => {
    e.preventDefault();
    const user = users.find(u => u.email === loginData.email && u.password === loginData.password);
    if (user) {
      if (user.role === 'provider' && !user.is_verified) {
        alert('Your provider account is pending admin approval.');
        return;
      }
      setCurrentUser(user);
      setUserRole(user.role);
      setIsLoggedIn(true);
    } else {
      alert('Invalid email or password');
    }
  };

  // --- REGISTER ---
  const handleRegister = (e) => {
    e.preventDefault();
    if (users.some(u => u.email === registerData.email)) {
      alert('Email already registered');
      return;
    }
    const newUser = {
      id: Math.max(...users.map(u => u.id)) + 1,
      ...registerData,
      is_verified: registerData.role === 'user' ? true : false // providers start unverified
    };
    setUsers([...users, newUser]);
    alert(registerData.role === 'user' 
      ? 'Registration successful! Please login.' 
      : 'Registration successful! Your account is pending admin approval.');
    setCurrentView('login');
  };

  // --- LOGOUT ---
  const handleLogout = () => {
    setIsLoggedIn(false);
    setCurrentUser(null);
    setLoginData({ email: '', password: '' });
  };

  // --- BOOKING ---
  const handleBookService = (service) => {
    setSelectedService(service);
    setBookingData({
      serviceId: service.id,
      date: '',
      time: '',
      address: currentUser?.address || '',
      notes: ''
    });
    setShowBookingModal(true);
  };

  const handleCreateBooking = (e) => {
    e.preventDefault();
    if (!bookingData.date || !bookingData.time) {
      alert('Please select both date and time.');
      return;
    }
    const newBooking = {
      id: bookings.length + 1,
      service_id: bookingData.serviceId,
      user_id: currentUser.id,
      provider_id: selectedService.provider_id,
      date: bookingData.date,
      time: bookingData.time,
      address: bookingData.address,
      notes: bookingData.notes,
      status: 'Pending'
    };
    setBookings([...bookings, newBooking]);
    setShowBookingModal(false);
    setBookingData({ serviceId: '', date: '', time: '', address: '', notes: '' });
    alert('Booking created successfully!');
  };

  const updateBookingStatus = (id, status) => {
    setBookings(bookings.map(b => b.id === id ? { ...b, status } : b));
  };

  // --- SERVICE MANAGEMENT (Provider) ---
  const handleAddService = (e) => {
    e.preventDefault();
    const service = {
      id: Math.max(...services.map(s => s.id), 0) + 1,
      ...newService,
      base_price_min: Number(newService.base_price_min),
      base_price_max: Number(newService.base_price_max),
      provider_id: currentUser.id
    };
    setServices([...services, service]);
    setNewService({ name: '', category: '', base_price_min: '', base_price_max: '' });
    setShowAddServiceModal(false);
    alert('Service added successfully!');
  };

  const handleDeleteService = (id) => {
    if (window.confirm('Are you sure you want to delete this service?')) {
      setServices(services.filter(s => s.id !== id));
      setBookings(bookings.filter(b => b.service_id !== id)); // Optional: cancel related bookings
    }
  };

  // --- USER MANAGEMENT (Admin) ---
  const handleApproveProvider = (id) => {
    setUsers(users.map(u => u.id === id ? { ...u, is_verified: true } : u));
    alert('Provider approved successfully!');
  };

  const handleDeleteUser = (id) => {
    if (window.confirm('Are you sure you want to delete this user?')) {
      setUsers(users.filter(u => u.id !== id));
      setServices(services.filter(s => s.provider_id !== id));
      setBookings(bookings.filter(b => b.user_id !== id && b.provider_id !== id));
    }
  };

  // --- UI HELPERS ---
  const getStatusColor = (status) => {
    switch (status) {
      case 'Pending': return 'bg-yellow-100 text-yellow-800';
      case 'Accepted': return 'bg-blue-100 text-blue-800';
      case 'In Progress': return 'bg-purple-100 text-purple-800';
      case 'Completed': return 'bg-green-100 text-green-800';
      case 'Cancelled': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'Pending': return <Clock className="w-4 h-4" />;
      case 'Accepted': return <UserCheck className="w-4 h-4" />;
      case 'In Progress': return <Play className="w-4 h-4" />;
      case 'Completed': return <CheckCircle className="w-4 h-4" />;
      case 'Cancelled': return <XCircle className="w-4 h-4" />;
      default: return <Clock className="w-4 h-4" />;
    }
  };

  // --- RENDER VIEWS ---
  const renderLogin = () => (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-indigo-100 p-4">
      <div className="bg-white p-8 rounded-2xl shadow-xl w-full max-w-md">
        <div className="text-center mb-8">
          <div className="mx-auto bg-gradient-to-r from-blue-500 to-indigo-600 w-16 h-16 rounded-full flex items-center justify-center mb-4">
            <Wrench className="w-8 h-8 text-white" />
          </div>
          <h1 className="text-3xl font-bold text-gray-900 mb-2">ODSM</h1>
          <p className="text-gray-600">On-Demand Service Management</p>
        </div>
        <form onSubmit={handleLogin} className="space-y-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Email</label>
            <div className="relative">
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <User className="h-5 w-5 text-gray-400" />
              </div>
              <input
                type="email"
                required
                value={loginData.email}
                onChange={(e) => setLoginData({ ...loginData, email: e.target.value })}
                className="w-full pl-10 px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                placeholder="Enter your email"
              />
            </div>
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Password</label>
            <div className="relative">
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <LockIcon />
              </div>
              <input
                type="password"
                required
                value={loginData.password}
                onChange={(e) => setLoginData({ ...loginData, password: e.target.value })}
                className="w-full pl-10 px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                placeholder="Enter your password"
              />
            </div>
          </div>
          <button
            type="submit"
            className="w-full bg-gradient-to-r from-blue-600 to-indigo-700 text-white py-3 px-4 rounded-lg hover:from-blue-700 hover:to-indigo-800 font-medium shadow-md"
          >
            Sign In
          </button>
          <div className="text-center">
            <p className="text-gray-600">
              Don't have an account?{' '}
              <button
                type="button"
                onClick={() => setCurrentView('register')}
                className="text-blue-600 hover:text-blue-800 font-medium"
              >
                Register now
              </button>
            </p>
          </div>
        </form>
      </div>
    </div>
  );

  const renderRegister = () => (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-indigo-100 p-4">
      <div className="bg-white p-8 rounded-2xl shadow-xl w-full max-w-md">
        <div className="text-center mb-8">
          <div className="mx-auto bg-gradient-to-r from-green-500 to-teal-600 w-16 h-16 rounded-full flex items-center justify-center mb-4">
            <UserPlus className="w-8 h-8 text-white" />
          </div>
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Create Account</h1>
          <p className="text-gray-600">Join our service platform</p>
        </div>
        <form onSubmit={handleRegister} className="space-y-4">
          {['name', 'email', 'password', 'phone', 'address'].map(field => (
            <div key={field}>
              <label className="block text-sm font-medium text-gray-700 mb-2 capitalize">{field}</label>
              <input
                type={field === 'password' ? 'password' : field === 'phone' ? 'tel' : 'text'}
                name={field}
                required
                value={registerData[field]}
                onChange={(e) => setRegisterData({ ...registerData, [field]: e.target.value })}
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                placeholder={`Enter your ${field}`}
              />
            </div>
          ))}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Account Type</label>
            <select
              value={registerData.role}
              onChange={(e) => setRegisterData({ ...registerData, role: e.target.value })}
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            >
              <option value="user">Customer</option>
              <option value="provider">Service Provider</option>
            </select>
          </div>
          <button
            type="submit"
